import axios from 'axios'

const state = {
  all: []
}

const mutations = {
  loadComments(state, comments) {
    state.all = comments
  }
}

const actions = {
  loadComments({ commit }) {
    const uri = 'http://localhost:3008/comments'
    axios.get(uri)
      .then(
        res => {
          let comments = res.data
          commit('loadComments', comments)
