export default {
  name: 'Todo',
  methods: {
    close(id) {
      this.todos = this.todos.map(t => {
        if (t.id === id) t.compl = true
        return t
      })
    }
  },
  computed: {
    filteredTodos() {
      return this.todos.filter(t => t.compl === false)
    }
  },
  data: () => ({
    seen: false,
    msg: 'click',
    todos: [
      {
        id: '1',
        text: '第一项',
        compl: false
      },
      {
        id: '2',
        text: '第二项',
        compl: false
      }
    ]
  })
}



